package loopDemo;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// for 
		
		for(int i=0; i<10; i+=2) {
			System.out.println(i);
		}
		System.out.println("for döngüsü bitti");
		
		// while
		int i = 1;
		while(i < 10) {
			System.out.println(i);
			i++;
		}
        System.out.println("while döngüsü biti");
	}

}
